import React from 'react';
import PropTypes from 'prop-types';
import { EuiButtonIcon, EuiFlexGroup, EuiFlexItem } from '@elastic/eui';
import { FormattedMessage, injectI18n } from '@kbn/i18n/react';
import { TooltipIcon } from '../tooltip_icon';

// This is what is being generated by render() from the Arg class. It is called in FunctionForm
const ArgSimpleFormUI = ({ children, required, valueMissing, onRemove, intl }) => {
  return (
    <EuiFlexGroup alignItems="center" gutterSize="s" className="canvasArg__form">
      <EuiFlexItem>{children}</EuiFlexItem>
      {valueMissing && (
        <EuiFlexItem grow={false}>
          <TooltipIcon
            position="left"
            icon="error"
            content={
              <FormattedMessage
                id="xpack.canvas.arg.form.requiredValueTooltip"
                defaultMessage="This argument is required, you should specify a value."
              />
            }
          />
        </EuiFlexItem>
      )}

      {!required && (
        <EuiButtonIcon
          color="danger"
          onClick={onRemove}
          iconType="trash"
          aria-label={intl.formatMessage({
            id: 'xpack.canvas.arg.form.removeButtonAriaLabel',
            defaultMessage: 'Remove',
          })}
          className="canvasArg__remove"
        />
      )}
    </EuiFlexGroup>
  );
};

ArgSimpleFormUI.propTypes = {
  children: PropTypes.node,
  required: PropTypes.bool,
  valueMissing: PropTypes.bool,
  onRemove: PropTypes.func.isRequired,
};

export const ArgSimpleForm = injectI18n(ArgSimpleFormUI);
